    private void jButtonProjectActionPerformed(final java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonProjectActionPerformed
        projectNow = true;
        this.setVisible(false);
    }//GEN-LAST:event_jButtonProjectActionPerformed

    private void jRadioButtonShareActionPerformed(final java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButtonShareActionPerformed
        jTextShare.setEditable(true);
        if(jTextShare.getText().equals(DEFAULT_EVENT_MESSAGE))
            jTextShare.setText("");
        jTextShare.requestFocus();
        jTextLocal.setForeground(new java.awt.Color(153, 153, 153));
        jTextLocal.setEditable(false);
    }//GEN-LAST:event_jRadioButtonShareActionPerformed

    private void jRadioButtonLocalActionPerformed(final java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRadioButtonLocalActionPerformed
        jTextLocal.setEditable(true);
        if(jTextLocal.getText().equals(DEFAULT_EVENT_MESSAGE))
            jTextLocal.setText("");
        jTextLocal.requestFocus();
        jTextShare.setEditable(false);
        jTextShare.setForeground(new java.awt.Color(153, 153, 153));
    }//GEN-LAST:event_jRadioButtonLocalActionPerformed

    private void jTextLocalFocusGained(final java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTextLocalFocusGained
        if(jTextLocal.getText().equals(DEFAULT_EVENT_MESSAGE))
            jTextLocal.setText("");
        jTextLocal.setForeground(new java.awt.Color(0, 0, 0));
        jTextShare.setForeground(new java.awt.Color(153, 153, 153));
        jTextShare.setEditable(false);
    }//GEN-LAST:event_jTextLocalFocusGained

    private void jTextShareFocusGained(final java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTextShareFocusGained
        if(jTextShare.getText().equals(DEFAULT_EVENT_MESSAGE))
            jTextShare.setText("");
        jTextShare.setForeground(new java.awt.Color(0, 0, 0));
        jTextLocal.setEditable(false);
        jTextLocal.setForeground(new java.awt.Color(153, 153, 153));
    }//GEN-LAST:event_jTextShareFocusGained

